    refresh_city_resources: function (){
        
        
        if(!current_city)
            return ;
        if(!current_city.id_city)
            return ;
        
        return $.ajax({
            
            url: "api/city.php",
            data:{
                
                GET_CITY_RESOURCES: true,
                id_city:current_city.id_city,
                id_player:ID_PLAYER,
                token:TOKEN
                
            },
            type: 'GET',
            beforeSend: function (xhr) {
                
            },
            success: function (data, textStatus, jqXHR) {
                
                if(!isJson(data)){
                  
                    console.log(data);
                    return ;
                    
                }
                
                var json_data = JSON.parse(data);
                
                if(json_data){
                    
                    current_city.coin  = json_data.coin;
                    current_city.wood  = json_data.wood;
                    current_city.food  = json_data.food;
                    current_city.stone = json_data.stone;
                    current_city.metal = json_data.metal;
                    current_city.pop   = json_data.pop;
                    
                    city_profile.refresh_resource_view();
                    current_city.LS = Math.ceil(Date.now()/1000);
                    
                }
                
            },
            error: function (jqXHR, textStatus, errorThrown) {
                
            }
            
        });
        
    },
    
    startIncreasePop: function(){

        var current_val    = Number(current_city.pop);
        var pop_cap        = Number(current_city.pop_cap);
        var tax_val        = Math.ceil(Number(current_city.taxs)*pop_cap/100);
        var estemated_val  = Math.ceil(pop_cap + Number(Number(current_city.helper) === 2 ? pop_cap*3/100*city_building.lvl[cityHasType(10)] : 0 )  - tax_val);
        var time_differane = Math.floor(Date.now()/1000) - current_city.pop_LSS;
        
        
        
        
        if(time_differane >= 60*6){
            city_profile.updateCityPop();
        }else{
            city_profile.updateCityPop(Number(60*6*1000 - time_differane*1000));
        }
        
        
        
        
        
    },
    updateCityPop: function (time_out){
        
        if(!time_out || time_out <0){
            time_out = 0;
        }
        console.log('timed_out is : '+time_out);
        setTimeout(function (){
            
            $.ajax({
                    
                url: "api/city.php",
                cache: false,
                data:{

                    UPDATE_CITY_POP: true,
                    id_city: current_city.id_city,
                    worship_place:cityHasType(10),
                    id_player:ID_PLAYER,
                    token:TOKEN

                },
                type: 'POST',
                beforeSend: function (xhr) {

                },
                success: function (data, textStatus, jqXHR) {

                    if(isJson(data)){

                        var json_data = JSON.parse(data);

                    }else{

                        alert(data);

                    }
                    if(json_data.state === "ok"){
                        
                        current_city.pop_LSS = Math.ceil(Date.now()/1000);
                        current_city.food  = json_data.city.food;
                        current_city.wood  = json_data.city.wood;
                        current_city.stone = json_data.city.stone;
                        current_city.metal = json_data.city.metal;
                        current_city.coin  = json_data.city.coin;
                        current_city.food_in  = json_data.city.food_in;
                        current_city.wood_in  = json_data.city.wood_in;
                        current_city.stone_in = json_data.city.stone_in;
                        current_city.metal_in = json_data.city.metal_in;
                        current_city.coin_in  = json_data.city.coin_in;
                        current_city.pop  = json_data.city.pop;
                        city_profile.refresh_resource_view();


                    }else{
                        alert_box.confirmDialog(`خطاء تعديل السكان بالمدينة ${json_data}`);
                    }
                    

                },
                error: function (jqXHR, textStatus, errorThrown) {

                }

            });  
            
        } , time_out);
        
        
        
    },
