function armyDismess(){
    
    
    
    var selected_type = $("#dialg_box .mili_building .middle-content .selected_sol").attr("army_type");
    var amount = $("#number-to-dismiss").val();
    if(Number(amount) < 1){
        alert_box.confirmMessage("لا يمكنك طرد هذة الكمية من المدينة");
        return ;
    }
    
    alert_box.confirmDialog(`تأكيد طرد ${amount} ${army_data[selected_type].ar_title}  ` , function (){
        
        $.ajax({
        
            url: "api/city.php",
            data:{
                DISMESS_CITY_ARMY:true,
                army_type:selected_type,
                amount:amount,
                id_city:current_city.id_city,
                id_player:ID_PLAYER,
                    token:TOKEN
            },
            type: 'POST',
            beforeSend: function (xhr) {
                console.log(this.data)
            },
            success: function (data, textStatus, jqXHR) {
                
                if(isJson(data)){
                    var json_data = JSON.parse(data);
                }else{
                    console.log(data);
                    alert(data);
                    return ;
                }
                
                $(".close-alert").click();
                
                if(json_data.state === "ok"){
                    
                    current_city.pop   = json_data.pop;
                    current_city.coin  = json_data.coin;
                    current_city.food  = json_data.food;
                    current_city.wood  = json_data.wood;
                    current_city.stone = json_data.stone;
                    current_city.metal = json_data.metal;
                    current_city[selected_type] -= amount;
                    current_city.pop_LSS  = Math.ceil($.now()/1000);
                    current_city.food_out = json_data.food_out;
                    
                    city_profile.refresh_resource_view();
                    city_profile.refresh_hero_view();
                    
                    
                    $("#dialg_box .mili_building .middle-content .selected_sol .amount").html( current_city[selected_type]);
                    
                }
                
                
            },
            error: function (jqXHR, textStatus, errorThrown) {

            }

        });
        
    });
    
    
}

