function resourcesRefresh(){
    
    var food_rate   = (current_city.food_in  - current_city.food_out )/(60*60);
    var wood_rate   = (current_city.wood_in  - current_city.wood_out )/(60*60);
    var stone_rate  = (current_city.stone_in - current_city.stone_out)/(60*60);
    var metal_rate  = (current_city.metal_in - current_city.metal_out)/(60*60);
    var coin_rate   = (current_city.coin_in  - current_city.coin_out )/(60*60);
    
    current_city.food   = food_rate  < 0 ? Math.max(0 , Number(current_city.food  ) + food_rate  ) : ((current_city.food  >= current_city.food_cap )  ? Number(current_city.food )  : Number(current_city.food ) + food_rate) ;
    current_city.wood   = wood_rate  < 0 ? Math.max(0 , Number(current_city.wood  ) + wood_rate  ) : ((current_city.wood  >= current_city.wood_cap )  ? Number(current_city.wood )  : Number(current_city.wood ) + wood_rate);
    current_city.coin   = coin_rate  < 0 ? Math.max(0 , Number(current_city.coin  ) + coin_rate  ) : ((current_city.coin  >= current_city.coin_cap )  ? Number(current_city.coin )  : Number(current_city.coin ) + coin_rate) ;
    current_city.stone  = stone_rate < 0 ? Math.max(0 , Number(current_city.stone ) + stone_rate ) : ((current_city.stone >= current_city.stone_cap)  ? Number(current_city.stone)  : Number(current_city.stone) + stone_rate);
    current_city.metal  = metal_rate < 0 ? Math.max(0 , Number(current_city.metal ) + metal_rate ) : ((current_city.metal >= current_city.metal_cap)  ? Number(current_city.metal)  : Number(current_city.metal) + metal_rate);
    
  
    
    $("#city-profile .resource").each(function (){
        $(this).children(".left").children(".count-wrapper ").html(Math.ceil(current_city[$(this).attr("data-resource")]));
    });
    
    
}

