/*UPGRADE BULDING*/

$(document).on("click", ".upgrade-btn", function () {

    if (!$(this).hasClass("disabled")) {

        var building_type = $(".box_header").attr("type");
        var building_lvl = $(".box_header").attr("lvl");
        var building_place = $(".box_header").attr("place");
        var building_ob = building_type;
        var id_city = current_city.id_city;
        var self_ = $(this);

        var upgrading_num = 0;

        for (var iii in TIMED_TASKS) {

            if (TIMED_TASKS[iii].task === "building" && Number(TIMED_TASKS[iii].id_city) === Number(current_city.id_city)) {

                upgrading_num++;

            }


        }

        /* if more than 3 building are upgrading*/
        if (Number(player_state.motiv) > Math.floor(Date.now() / 1000)) {

            if (upgrading_num > 2) {

                alert_box.confirmMessage("لا يمكنك تطوير اكثر من 3 مبانى فى نفس الوقت");
                return;
            }

        } else {

            if (upgrading_num > 0) {


                alert_box.confirmDialog("عدد البنائين واحد فقط عليك تشغيل خطبة تحفيزية لزيادة عدد البنائين الى ثلاثة", function () {

                    var matrial_to_use = [
                        "motiv_60",
                        "motiv_7"
                    ];

                    BoxOfMatrialToUse(matrial_to_use, "add_city_builder");

                });
                return;
            }

        }

        if (Building.isUpgradable(building_type, building_lvl) === true) {

            $.ajax({

                url: "api/building.php",
                data: {
                    upgrade_building: true,
                    place: building_place,
                    type: building_type,
                    lvl: building_lvl,
                    id_city: id_city,
                    id_player: ID_PLAYER,
                    worship_helper: Number(current_city.helper) === 1 ? city_building.lvl[cityHasType(BUILDING_TYPS.WORSHIP)] : 0,
                    token: TOKEN
                },

                type: 'POST',

                beforeSend: function (xhr) {

                },
                success: function (data, textStatus, jqXHR) {

                    if (!isJson(data)) {
                        alert_box.confirmMessage("حدث خطاء");
                        buildingClick(building_place, true);
                        return;
                    }


                    var json_data = JSON.parse(data);

                    if (json_data.state === "ok") {

                        TIMED_TASKS = json_data.timedTasks;


                        buildingClick(building_place, true);
                        refreshTasksList();
                        building_hammer_animate(json_data.building_place);

                        if (Number(building_type) === BUILDING_TYPS.PALACE && Number(building_lvl) >= 25) {
                            matrial_player.matrial_flags.law_3 -= 5;
                        } else if (Number(building_type) === BUILDING_TYPS.PALACE && Number(building_lvl) >= 25) {
                            matrial_player.matrial_flags.law_2 -= 5;
                        } else if (Number(building_type) === BUILDING_TYPS.PALACE && Number(building_lvl) >= 10) {
                            var amount_array = [5, 5, 5, 8, 8, 8, 10, 10, 10, 10];
                            matrial_player.matrial_flags.law_1 -= amount_array[building_lvl - 10];
                        }

                        current_city.food = json_data.city_res.food;
                        current_city.wood = json_data.city_res.wood;
                        current_city.stone = json_data.city_res.stone;
                        current_city.metal = json_data.city_res.metal;
                        current_city.coin = json_data.city_res.coin;
                        city_profile.refresh_resource_view();

                    } else {

                        alert_box.confirmMessage("حدث خطأ");
                        console.log(data);

                    }
                },
                error: function (jqXHR, textStatus, errorThrown) {

                }

            });

        } else {

            alert_box.confirmMessage("لا يمكنك تطوير المبنى الحالى ");
            buildingClick(building_place, true);

        }

    }

});



