function afterBuildinConstructing(buiding_type) {

    Player_profile.refresh_player_data();

    if (Number(buiding_type) === BUILDING_TYPS.COTTAGE) {

        current_city.pop_cap = Number(current_city.pop_cap) + 250;

    } else if (Number(buiding_type) === BUILDING_TYPS.STORE) {

        current_city.city_storage.total_cap = Number(current_city.city_storage.total_cap) + 80000;

        var food_cap = Math.floor(current_city.city_storage.food_storage_ratio * current_city.city_storage.total_cap / 100);
        var wood_cap = Math.floor(current_city.city_storage.wood_storage_ratio * current_city.city_storage.total_cap / 100);
        var stone_cap = Math.floor(current_city.city_storage.stone_storage_ratio * current_city.city_storage.total_cap / 100);
        var metal_cap = Math.floor(current_city.city_storage.metal_storage_ratio * current_city.city_storage.total_cap / 100);


        current_city.food_cap = food_cap;
        current_city.wood_cap = wood_cap;
        current_city.stone_cap = stone_cap;
        current_city.metal_cap = metal_cap;

        city_profile.refresh_resource_view();

    }

    alert_box.succesMessage(`تم اضافة المبنى بنجاح`);

}



