 function getMinSpeed()
 {
       var min = 900; // fastest speed
       
       if(min > speeds[current_hero_army.f_1_type] && Number(current_hero_army.f_1_type) !== 0){
           
           min = speeds[current_hero_army.f_1_type];
           
       } 
       if(min > speeds[current_hero_army.f_2_type] && Number(current_hero_army.f_2_type) !== 0){
           
           min = speeds[current_hero_army.f_2_type];
           
       } 
       if(min > speeds[current_hero_army.f_3_type] && Number(current_hero_army.f_3_type) !== 0){
           
           min = speeds[current_hero_army.f_3_type];
           
       } 
       if(min > speeds[current_hero_army.b_1_type] && Number(current_hero_army.b_1_type) !== 0){
           
           min = speeds[current_hero_army.b_1_type];
           
       } 
       if(min > speeds[current_hero_army.b_2_type] && Number(current_hero_army.b_2_type) !== 0){
           
           min = speeds[current_hero_army.b_2_type];
           
       } 
       if(min > speeds[current_hero_army.b_3_type] && Number(current_hero_army.b_3_type) !== 0){
           
           min = speeds[current_hero_army.b_3_type];
           
       } 
       return min;
 }
 
 /*   calculate time needed to attak */
function calAttakTime()
{
    
    var x_coord = battel_data.x_coord;
    var y_coord = battel_data.y_coord;
    var type = Number(WorldUnit.getWorldUnit(battel_data.x_coord, battel_data.y_coord).t);

    if(!x_coord || !y_coord || !type){
        return (0);
    }
    
    if(WorldUnit.isAsianSquads(type) 
            || WorldUnit.isGangStar(type) 
            || WorldUnit.isCarthagianArmies(type)
            || WorldUnit.isArenaChallange(type)
            || WorldUnit.isArenaDeath(type)
            || WorldUnit.isArmyCapital(type)
            || WorldUnit.isMonawrat(type)){
        battel_data.time = 120;
        return 120;
    }else if(WorldUnit.isArenaGuild(type)){
        return  5*60;
    }else if(WorldUnit.isCity(type)){
        
        var dist = getDistance(x_coord , y_coord);
        battel_data.time = Math.ceil(dist/getMinSpeed());
        return Math.max(Math.ceil(dist/getMinSpeed()) , 15*60);
        
    }else{
         var dist = getDistance(x_coord , y_coord);
        battel_data.time = Math.ceil(dist/getMinSpeed());
        return Math.ceil(dist/getMinSpeed());
    }
    
    
}


