    getEquipList: function (  offset , part ){
        
        if(isNaN(offset)){
            offset = 0;
        }
        
        if(typeof part !== "string"){
            part = "all";
        }
        
        
        var all_equip = "";
        var conter = 0;
        var total = 0;
        
        if(available_Equip !== null){
            
            
            
            var end = Math.min(available_Equip.length - offset , 24);
            
            for(var iii = 0 + offset;  iii< available_Equip.length ; iii++){
                
                if((part ===  available_Equip[iii]["part"]  || part === "all")){
                    
                   if(conter < end){
                       
                        var unit_type = available_Equip[iii]["type"];
                        var unit_part = available_Equip[iii]["part"];
                        var unit_lvl  = available_Equip[iii]["lvl"];
                        
                        all_equip  += `                 <div class="unite-eq">
                                                               <button style="background-image: url(${Equipment.getImage(unit_type , unit_part, unit_lvl - 1)})" 
                                                            id_equip ="${available_Equip[iii]["id_equip"]}"
                                                                    equip_part="${available_Equip[iii]["part"]}"
                                                                    equip_type="${available_Equip[iii]["type"]}"
                                                                    data-offset="${iii}"
                                                                    class="avail_equip putable-equi equip-unit" data-equi-part="${unit_part}" data-equi-lvl="${unit_lvl}" data-equi-type="${unit_type}"
                                                                   ></button>
                                                        </div>  `;
                        conter++;
                   }
                    
                    total ++ ;
                    
                }
                
                
            }
            
            for(var jjj = 0 ; jjj < 24 - conter ; jjj ++ ){
                all_equip  += `                 <div class="unite-eq">
                                                           <button disabled="disabled" class="avail_equip" style="background-image: url(images/icons/hero/eq-bg.png)"></button>
                                                    </div>  `;
            }
            
        }
        var i = Math.ceil(offset/24) + 1;
        var t = Math.floor(total/24) + 1;
        $("#navigate-btn .lable").html( i + "/" + t) ;
        return all_equip;
    },
