$(document).on("click" , "#left-down , #right-down" , function (){
    
    // get id_hero
    var id_hero = $(this).attr("id_hero");
    
    if(!heroAvailableForTask(id_hero)){
        $("body").append(alert_box.confirmMessage("لا يمكن نقل القوات </br> البطل فى مهمة"));
        return false ;
        
    }
    
    if($(this).attr("id") === "left-down"){
       if(getHeroCapById(current_hero.id_hero) <= 0) {
            return ;
       }
    }else{
        if(getHeroCapById(SECOND_HERO.id_hero) <= 0) {
            return ;
       }
    }
    
    var id_city = current_city.id_city;
    var this_ = $(this);
    
    $.ajax({
        url: "api/hero.php",
        data: {
            CLEAR_HERO: true,
            id_hero:id_hero,
            id_city:id_city,
            id_player:ID_PLAYER,
            token:TOKEN
        },
        type: 'get',
        beforeSend: function (xhr) {
            waitCursor();
            $("#left-down , #right-down").attr("disabled" , "disabled");
        },
        success: function (data, textStatus, jqXHR) {
            unwaitCursor();
            $("#left-down , #right-down").removeAttr("disabled");
            if(isJson(data)){
                var json_data = JSON.parse(data);
            }else{
                alert(data);
                return ;
            }
            
            current_city.army_a = Number(json_data.army_city.army_a);
            current_city.army_b = Number(json_data.army_city.army_b);
            current_city.army_c = Number(json_data.army_city.army_c);
            current_city.army_d = Number(json_data.army_city.army_d);
            current_city.army_e = Number(json_data.army_city.army_e);
            current_city.army_f = Number(json_data.army_city.army_f);
            
            $("#down-trade-army").html(army.downTradeArmy());
            if(json_data.state !== "ok"){
                alert_box.confirmMessage("حدث خطاء");
                return ;
            }
            var all_list;
            if(this_.attr("id") === "left-down"){
                
                
                current_hero.army.f_1_num = 0;
                current_hero.army.f_2_num = 0;
                current_hero.army.f_3_num = 0;
                current_hero.army.b_1_num = 0;
                current_hero.army.b_2_num = 0;
                current_hero.army.b_3_num = 0;
                current_hero.army.f_1_type = 0;
                current_hero.army.f_2_type = 0;
                current_hero.army.f_3_type = 0;
                current_hero.army.b_1_type = 0;
                current_hero.army.b_2_type = 0;
                current_hero.army.b_3_type = 0;
                all_list = $(".middle-content").children(".army_container").children().children("ol").children(); 
                $(".hero-1  ol li:nth-child(2) .header-2:nth-child(2)").html(0 + "/"+getHeroMaxCap(current_hero));
               
            }else if(this_.attr("id") === "right-down"){
                
                SECOND_HERO.army.f_1_num = 0;
                SECOND_HERO.army.f_2_num = 0;
                SECOND_HERO.army.f_3_num = 0;
                SECOND_HERO.army.b_1_num = 0;
                SECOND_HERO.army.b_2_num = 0;
                SECOND_HERO.army.b_3_num = 0;
                SECOND_HERO.army.f_1_type = 0;
                SECOND_HERO.army.f_2_type = 0;
                SECOND_HERO.army.f_3_type = 0;
                SECOND_HERO.army.b_1_type = 0;
                SECOND_HERO.army.b_2_type = 0;
                SECOND_HERO.army.b_3_type = 0;
                all_list = $(".right-content").children(".army_container").children().children("ol").children(); 
                $(".hero-2  ol li:nth-child(2) .header-2:nth-child(2)").html(0 + "/"+getHeroMaxCap(SECOND_HERO));
                
            }
            all_list.each(function (){
                $(this).attr("army-type" , "sol-0");

                $(this).children("img").attr("army-type" , "sol-0");
                $(this).children("img").attr("src" , "images/tech/no_army.png");
                $(this).children(".amount").html("0");
                $(this).children(".amount").attr("amount" , 0);

            });
            
            city_profile.refresh_army_view();
            army.refreshArmy_leftTrade();
            army.refreshArmy_rightTrade();
        },
        error: function (jqXHR, textStatus, errorThrown) {
            console.log(jqXHR);
        }
    });
    
});


