function findMaxNumForArmyBuild(army_type)
{
    var army_obj = army_data[army_type];
    
    var min_num = parseInt(current_city.food/army_obj.food);
    
    if( army_obj.coin !== 0 &&  (current_city.coin/army_obj.coin) < min_num ){
        
        min_num = parseInt(current_city.coin/army_obj.coin);
        
    }
    if( army_obj.wood !== 0 && (current_city.wood/army_obj.wood) < min_num){
        
        min_num = parseInt(current_city.wood/army_obj.wood);
        
    }
    if( army_obj.stone !== 0 && (current_city.stone/army_obj.stone) < min_num ){
        
        min_num = parseInt(current_city.stone/army_obj.stone);
        
    }
    if( army_obj.metal !== 0 &&  (current_city.metal/army_obj.metal)  < min_num){
        
        min_num = parseInt(current_city.metal/army_obj.metal);
        
    }
    if( army_obj.people !== 0 && (current_city.pop/army_obj.people)  < min_num){
        
        min_num = parseInt(current_city.pop/army_obj.people);
        
    }
    
    
     if(army_type === "wall_a" || army_type === "wall_b" || army_type === "wall_c"){
        var total_space = Number(current_city.wall_a)*1 
                + Number(current_city.wall_b)*3 
                + Number(current_city.wall_c)*5;
                
        
        min_num = Math.min(Math.max( current_city.wall_cap  - total_space , 0) /army_obj.wall_space , min_num);
    }
   
    
    return min_num;
}

