function finishArmyBuild(index){
    
    
    var id_city   = TIMED_TASKS[index].id_city;
    var place     = TIMED_TASKS[index].place;
    var amount    = TIMED_TASKS[index].amount;
    var army_type = TIMED_TASKS[index].army_type;
    var id_worker = TIMED_TASKS[index].id;
    if(!current_city)
        return ;
    else if(!current_city.id_city)
        return ;
    
    $.ajax({
        url: "api/army.php",
        data: {
            FINISH_ARMY_BUILD: true,
            place: place,
            id_city: id_city,
            amount: amount,
            army_type: army_type,
            id_player:ID_PLAYER,
            current_city:current_city.id_city,
            token:TOKEN,
            id_work:id_worker
        },
        type: 'POST',
        beforeSend: function (xhr) {
            
        },
        success: function (data, textStatus, jqXHR) {
            console.log(data)
            if(isJson(data)){
                var json_data = JSON.parse(data);
            }else{
                alert(data);
            }

            if (Number(current_city.id_city) === Number(id_city) && json_data.state === "ok") {
                
                current_city.food_out = json_data.city.food_out;
                current_city.food  = json_data.city.food;
                current_city.wood  = json_data.city.wood;
                current_city.metal = json_data.city.metal;
                current_city.stone = json_data.city.stone;
                current_city.coin  = json_data.city.coin;
                
                current_city.army_a = json_data.city.army_a;
                current_city.army_b = json_data.city.army_b;
                current_city.army_c = json_data.city.army_c;
                current_city.army_d = json_data.city.army_d;
                current_city.army_e = json_data.city.army_e;
                current_city.army_f = json_data.city.army_f;
                
                player.honor    = json_data.player.honor;
                player.prestige = json_data.player.prestige;
                player.gold     = json_data.player.gold;
                player.guild    = json_data.player.guild;
                
                var id_console = current_city.console;
                if(id_console){
                   var hero =  getHeroById(id_console);
                   hero.exp = Number(hero.exp) + Number(json_data.prestige)*2;
                   $("#dialg_box[type='hero'] .hero-profile").replaceWith(army.middle_content(hero));
                }
                
                $(".current-working").each(function () {
                    if (Number($(this).attr("id_work")) === Number(id_worker)) {
                        $(this).next(".current-working").children(".current-unite").children(".time-rest").children(".counter").addClass("time_counter");
                        $(this).remove();
                        return;

                    }
                });



                $(".middle-content .soldier").each(function () {

                    $(this).children(".amount").html((current_city[$(this).attr("army_type")]));

                });

            }
            Player_profile.refresh_view();
            city_profile.refresh_army_view();
            city_profile.refresh_resource_view();
        }
        ,
        error: function (jqXHR, textStatus, errorThrown) {

        }


    });
    TIMED_TASKS.splice(index, 1);
    
}


