function buyMatrial(matrial , amount)
{
    
    if(amount === undefined){
        amount = Number($("#amount_to_buy").val()) || 1;
    }
    
    if(!isInt(Number(amount))){
        
        alert_box.failMessage("لا يمكن ان تكون الارقام عشرية");
        return ;
        
    }
    
    if(amount <=0){
        alert_box.failMessage("لا يمكن  شراء هذة الكمية");
        return ;
    }
    
    if(player.gold < matrial_all[matrial].gold*amount){
        
        $("body").append(alert_box.confirmMessage(" ليس لديك قطع ذهب كافية<br> <a href='gold_buy.php'> لشراء ذهب اضغط هنا</a>"));
        return ;
        
    }
    
    
    $.ajax({
        
        url: "api/matrial.php",
        data: {
            
            BUY_MATRIAL: true,
            matrial: matrial,
            amount: amount,
            id_player:ID_PLAYER,
                    token:TOKEN
            
        },
        type: 'POST',
        beforeSend: function (xhr) {
            
        },
        success: function (data, textStatus, jqXHR) {
       
            if(data === "done"){
                
                player.gold -= matrial_all[matrial].gold*amount;
                
                $("#player_gold span").html(getArabicNumbers(player.gold));
                $(".close-alert_container").click();
                
                matrial_player[matrial_all[matrial].table][matrial] = Number(matrial_player[matrial_all[matrial].table][matrial] ) + amount;
                alert_box.succesMessage("تمت عملية الشراء بنجاح");
                $(".matrial_unit").each(function (){
                    
                    if($(this).attr("matrial_type") === matrial){
                        
                        $(this).children(".img-inside-box").children(".player_amount").children("p").html(getArabicNumbers(matrial_player[matrial_all[matrial].table][matrial]));
                        
                    }
                       
                     $(".budget .txt").html("لديك:" + getArabicNumbers(player.gold));
                });
                
            }else{
                
                alert(data);
                
            }
            
        },
        error: function (jqXHR, textStatus, errorThrown) {
            
        }
        
    });
    
    
}






