var Matrial = {
    
    getPlayerAmount: function (matrial){
        
        return Number(matrial_player[matrial_all[matrial].table][matrial]);
        
    },
    getMatrial: function (matrial){
        
        return matrial_all[matrial];
        
    },
    getMatrialName : function (matrial){
        
        return matrial_all[matrial].name;
        
    },
    givePlayer: function (matrial , amount){
        if(!amount ){
            amount = 1;
        }
        if(!matrial_all[matrial]){
            return ;
        }
        matrial_player[matrial_all[matrial].table][matrial] =
                Number(matrial_player[matrial_all[matrial].table][matrial]) + Number(amount);   
    },
    takeFrom: function (matrial , amount){
        if(!amount ){
            amount = 1;
        }
        if(!matrial) return ;
        
        matrial_player[matrial_all[matrial].table][matrial] -= Number(amount);   
    },
     takeNeedsForAttack: function (type){
        
        if(WorldUnit.isAsianSquads(type)){
            Matrial.takeFrom("truce_pack" , 1);
        }else if(WorldUnit.isGangStar(type)){
            Matrial.takeFrom("t_map" , 1);
        }else if(WorldUnit.isCamp(type) || WorldUnit.isMonawrat(type)){
           Matrial.takeFrom( "necklace_4" , 1) ;
        }
        
    },
    image: function (mat){
        return matrial_all[mat].image;
    },
    table: function (mat){
        return matrial_all[mat].db_tab;
    }
    
};



Matrial.prizeToString = function (data){
    
    var stringArray = [];
    
    for(var jjj in data.players){
        
        if(Number(data.players[jjj].id_player) !== Number(data.attacker))
            continue;
        
        var prizeList =  data.players[jjj].prize.matrial;
        for(var iii in prizeList){
            
            stringArray.push(` x ${prizeList[iii].amount} ${this.getMatrialName(prizeList[iii].prize)}`);

        }
        
    }
    
    
    
    return stringArray.join(" , ");
    
};
