    checkCondetions: function (study_type, lvl) {
        var obj = edu_acad[study_type] || edu_uni[study_type];

        var condetions = obj.getCondetion(lvl);

        for (var one in condetions) {

            if (condetions[one].con_type === "building") {
                if (Building.BuildingWithMaxLvl(condetions[one].building_type) < condetions[one].building_lvl) {
                    return  false;
                }

            } else if (condetions[one].con_type === "matrial") {

                if (matrial_player[matrial_all[condetions[one].matrial].table][condetions[one].matrial] < condetions[one].amount) {
                    return false;
                }

            }
        }

        return true;
    },

    isUpgradable: function (study, lvl) {
        if (Number(lvl) >= 30) {
            return false;
        } else if (current_city.food < this.calResource.food(study, lvl)) {
            return false;
        } else if (current_city.wood < this.calResource.wood(study, lvl)) {
            return false;
        } else if (current_city.stone < this.calResource.stone(study, lvl)) {
            return false;
        } else if (current_city.metal < this.calResource.metal(study, lvl)) {
            return false;
        } else if (current_city.coin < this.calResource.coin(study, lvl)) {
            return false;
        } else if (!this.checkCondetions(study, lvl)) {
            return false;
        } else {
            return true;
        }
    },
