function canBuildNewCity(x_coord , y_coord)
{
    var city_counts = PLAYER_ALL_CITIES.length
    if(!WorldUnit.isEmpty(WorldUnit.getWorldUnit(x_coord , y_coord).t)){
        return false;
    }else if(Number(current_city.food) < Math.pow(10,city_counts + 3)){
        return false;
    }else if(Number(current_city.wood) < Math.pow(10,city_counts + 3)){
        return false;
    }else if(Number(current_city.stone) < Math.pow(10,city_counts + 3)){
        return false;
    }else if(Number(current_city.metal) < Math.pow(10,city_counts + 3)){
        return false;
    }else if(Number(current_city.coin) < Math.pow(10,city_counts + 3)){
        return false;
    }else if(Number(player.porm) < city_counts*2){
        return false;
    } 
    
    return true;
    
}


function getDistance(x , y){
    return (Math.floor(Math.sqrt(Math.pow((current_city.x - x),2) + Math.pow((current_city.y - y),2))*6000));
}


