Elkaisar.World.Map.posX = function (xCoord, yCoord){  return xCoord*64 - yCoord*64;};

Elkaisar.World.Map.posY = function (xCoord, yCoord){ return xCoord*32 + yCoord*32;};


Elkaisar.World.Map.xCoord = function (x, y){ return Math.round((2*y + x)/128);};
Elkaisar.World.Map.yCoord = function (x, y){ return Math.round((2*y - x)/128);};

Elkaisar.World.Map.widthInTile  = function (){ return Math.ceil(Elkaisar.MAX_SCREEN_WIDTH/128); };
Elkaisar.World.Map.heightInTile = function (){ return Math.ceil(Elkaisar.MAX_SCREEN_HEIGHT/64); };

Elkaisar.World.Map.posY = function (xCoord, yCoord){
    return xCoord*32 + yCoord*32;
};

Elkaisar.World.Map.realCoord = function (coord){
    return (500*500 + Number(coord))%500;
};

Elkaisar.World.Map.posZ = function (xCoord, yCoord){
    
    var z_offset_x = (xCoord) < 0 ? -500 :(xCoord)  > 499  ? 500 : 1;
    var z_offset_y = (yCoord) < 0 ? -500 :(yCoord)  > 499  ? 500 :  1;
    
    return  500*500*500 + Math.max(this.realCoord(xCoord) + this.realCoord(yCoord))+z_offset_y+ z_offset_x;
    
};

Elkaisar.World.Map.getEntity = function (type , lvl){
   
  
    if(WorldUnit.isRiver(type) || WorldUnit.isEmpty(type)){
        return Crafty.e("2D, Canvas , Mouse , WorldUnit, worldEnt").attr({_w:128,_h:128});
    }
    
    return Crafty.e(`2D, Canvas, ${Elkaisar.World.UnitData[type].tileName[lvl] || Elkaisar.World.UnitData[type].tileName[0]}, Mouse , WorldUnit, worldEnt`) ;

};


