function getUnitesFromDb(x_s , x_e , y_s , y_e)
{
    
    var last_move_index = Crafty.WORLD_MOVES;
    Crafty.WORLD_MOVES++;
    
    
    var x_floor_s = Math.ceil((Math.min(x_s, x_e) - 20)/10)*10;
    var y_floor_s = Math.ceil((Math.min(y_s, y_e)  - 20)/10)*10;
    var x_floor_e = x_floor_s + 60;
    var y_floor_e = y_floor_s + 60;
    var counter = 0;
    
    for(var xx = x_floor_s ; xx <= x_floor_e; xx += 10){
        for(var yy = y_floor_s ; yy <= y_floor_e ; yy +=10){
            counter++;
            addMapWorldFloor({
                x:xx,
                y:yy
            });
        }
    }
        
        
       
    
    for(var xxx = x_s ; xxx <= x_e; xxx++){
        for(var yyy = y_s ; yyy <= y_e ; yyy++){

            addMapUnite({
                x:xxx,
                y:yyy
            } , last_move_index);
        }
    }

  
    
    
   Crafty("UnitToolTip").each(function (){this.destroy();});  

   
    
    
    $.ajax({
        url: "api/world.php",
        data: {
            GET_SECONDRY_UNITE_DATA:true,
            x_start : Elkaisar.World.Map.realCoord(x_s),
            x_end   : Elkaisar.World.Map.realCoord(x_e),
            y_start : Elkaisar.World.Map.realCoord(y_s),
            y_end   : Elkaisar.World.Map.realCoord(y_e)
        },
        type: 'GET',
        beforeSend: function (xhr) {

        },
        success: function (data, textStatus, jqXHR) {
            
            if(isJson(data)){
                var json_data = JSON.parse(data);
            }
          
            for(var one_city in json_data.city){
                Animation.cityFlag(json_data.city[one_city]);
            }
            
            for(var fire in json_data.fired){
                Animation.fireWorldUnit(json_data.fired[fire]);
            }
            
            for(var unit in json_data.changableLvl){
                
               Elkaisar.worldAllUnits[json_data.changableLvl[unit].x*500 + Number(json_data.changableLvl[unit].y)].l = json_data.changableLvl[unit].l;
            }
          
        },
        error: function (jqXHR, textStatus, errorThrown) {
            console.log(errorThrown);
        }
       
    });
    
}



Elkaisar.World.Map.clear = function(){
    
   if(Crafty("*").length < 500)
        return ;
    
   var rect = Crafty.viewport.rect();
   rect._x -= Elkaisar.World.unitWidth*3;
   rect._y -= Elkaisar.World.unitHeight*3;
   rect._h += Elkaisar.World.unitHeight*6;
   rect._w += Elkaisar.World.unitWidth*6;
   
   setTimeout(function (){
        Crafty("2D").each(function (){
           
            if(!this.intersect(rect) && $.isFunction(this.destroy)){
                
                    this.destroy();
                    delete WorldUnit.getWorldUnit(this.coord_x , this.coord_y).entite;
                    delete WorldUnit.getWorldUnit(this.coord_x , this.coord_y).floor;
                    delete WorldUnit.getWorldUnit(this.coord_x , this.coord_y).cityFlag;
                
            }
        });
    }, 0, rect);
   
};


