function battelStart(){
    
    if(Number(current_hero.in_city )!== 1){
        $(".close-alert").click();
        alert_box.failMessage("لا يمكنك ارسال بطل الى بعثة و هو فى ليس داخل المدينة");
        return ;
    }
    
    var _wu = WorldUnit.getWorldUnit(battel_data.x_coord , battel_data.y_coord);
    
    if(!Elkaisar.World.UnitData.isPromLvlOk(_wu.t)){
        alert_box.failMessage("لا يمكنك الهجو..م تحقق من توافق الشروط");
        return ;
    }
    
    var out_hero  = 0;
    for(var ii in city_heros){
        
        if(Number(city_heros[ii].in_city) !== 1){
            out_hero++;
        }
        
    }
    
    var blaza_place = cityHasType(BUILDING_TYPS.HOSPITAL);
    
    if(!blaza_place){
        $(".close-alert").click();
        alert_box.failMessage("لا يمكنك ارسال بعثات خارج المدينة حيث لا يوجد بلازا داخل المدينة");
        return ;
    }
    
    var blaza_lvl   = Math.min(Number(city_building.lvl[blaza_place]) , 20);
    
    if(out_hero >= blaza_lvl){
        $(".close-alert").click();
        alert_box.failMessage("وصل عدد الابطال بالخارج الى الحد الاقصى لا يمكنك ارسال قوات اخرى</br> (يمكنك الانتظار حتى عودة الابطال من الخارج)");
        return ;
    }
    
    
    if(!Battel.isAttackable(current_hero.id_hero, WorldUnit.getWorldUnit(battel_data.x_coord , battel_data.y_coord).t)){
        
        alert_box.failMessage("لا يمكنك الهجوم بالبطل الحالى! ");
        return ;
        
    }
    
    if(Number(battel_data.task) === BATTEL_TYPES_CONST.SUPPLY){
        
        Battel.millertySupply(battel_data.x_coord , battel_data.y_coord);
        getHeroById(current_hero.id_hero).in_city = 0;
        city_profile.refresh_hero_view();
        return ;
        
    }
    
    if(Number(battel_data.task) === BATTEL_JOIN_ATTACK  
            || Number(battel_data.task) === BATTEL_JOIN_DEFENCE ){
        
        
        if(!Battel.checkJoinReq(battel_data.x_coord , battel_data.y_coord)){
            
            alert_box.failMessage(`لا يوجد لديك مواد كافية للانضمام`);
            return ;
        }
        
        $.ajax({
            
            url: "api/battel.php",
            data:{
                
                JOIN_BATTEL:true,
                id_battel  :battel_data.id_battel,
                id_hero    :current_hero.id_hero,
                side       :battel_data.side,
                id_city    :current_hero.id_city,
                id_player  :ID_PLAYER,
                token      :TOKEN
                
            },
            type: 'POST',
            beforeSend: function (xhr) {
                
            },
            success: function (data, textStatus, jqXHR) {
                
                if(isJson(data)){
                    var jsonData = JSON.parse(data);
                }else{
                    alert(data);
                }
                
                
                if(jsonData.state === "ok"){

                    $(".close_dialog").trigger("click");
                    current_hero.in_city = 0;
                    $(".close-alert").trigger("click");
                    
                    battel_data.type = jsonData.unit_type;
                    battel_data.lvl  = jsonData.unit_lvl;
                    Hero.heroAttackProc();
                    
                    PLAYER_NOTIF.hero_in_battel = Number(PLAYER_NOTIF.hero_in_battel) + 1;
                    city_profile.refresh_hero_view();
                    
                    
                    var battel = {
                        attack_num  : jsonData.attack_num,
                        city_name   : jsonData.c_name,
                        defence_num : jsonData.defence_num,
                        defence_side: { t: battel_data.type, l: battel_data.lvl },
                        id_battel   : battel_data.id_battel,
                        player_name : jsonData.p_name,
                        time_end    : jsonData.time_end,
                        time_start  : jsonData.time_start,
                        id_hero     : current_hero.id_hero,
                        x_coord     : battel_data.x_coord,
                        x_city      : jsonData.x_city,
                        y_coord     : battel_data.y_coord,
                        y_city      : jsonData.y_city
                        
                    };
                    
                    var found = false;
                    for(var iii in current_battels){
                        
                        if(Number(current_battels[iii].id_battel) ===  Number(battel_data.id_battel)){
                            found = true;
                        }
                        
                    }
                    
                    if(!found){
                        PLAYER_NOTIF.battel_number =  Number(PLAYER_NOTIF.battel_number) + 1;
                        
                        
                        if(!current_battels){
                            current_battels = [battel];
                        }else{
                            current_battels.push(battel);
                        }
                    }
                    Fixed.refreshPlayerNotif();
                    Battel.afterJoin(battel_data.x_coord , battel_data.y_coord);
                    
                    ws.send(JSON.stringify({
                        type:"AFTER_JOIN_WATCH_LIST",
                        side: battel_data.side,
                        x_coord: battel_data.x_coord,
                        y_coord: battel_data.y_coord,
                        id_player: ID_PLAYER,
                        id_battel:battel_data.id_battel,
                        attack_num: jsonData.attack_num,
                        defence_num:jsonData.defence_num,
                        token:TOKEN
                    }));
                    

                }else if(jsonData.state === "error_1"){
                    alert_box.confirmMessage("انتهت المعركة لا يمكنك الانضمام");
                    
                } else if(jsonData.state === "error_2_1"){
                    alert_box.confirmMessage("  لا يمكنك الانضمام للهجوم </br> (وصل عدد المهاجمين الى الحد الاقصى)");
                    
                }else if(jsonData.state === "error_2_2"){
                    
                    alert_box.confirmMessage("  لا يمكنك الانضمام للدفاع </br> (وصل عدد المدافعين الى الحد الاقصى)");
                    
                } else if(jsonData.state === "error_3_1"){
                    
                    alert_box.confirmMessage(" غير مسموح لك بالانضمام للهجوم");
                    
                } else if(jsonData.state === "error_3_2"){
                    
                    alert_box.confirmMessage(" غير مسموح لك بالانضمام للدفاع");
                    
                }  else{
                    alert_box.confirmMessage("لا يمكنك الانضمام");
                    
                }           
            },
            error: function (jqXHR, textStatus, errorThrown) {
                
            }
            
        });
        
        
    }else{
        
         ws.send(
            JSON.stringify({
                url:"WS_Battel/start",
                data:{
                    x_to:battel_data.x_coord,  
                    y_to: battel_data.y_coord,
                    x_from: current_city.x,
                    y_from: current_city.y,
                    hero_lead: current_hero.id_hero,
                    idPlayer:ID_PLAYER,
                    attack_task: battel_data.task,
                    token:TOKEN,
                    p_name:player.name,
                    id_guild:player.id_guild || -1,
                    guild_name:player.guild || "----"
                },
                id_player:ID_PLAYER

            })
        );
        
        
    }
    
}



