GodGate.askToOpenGate = function (gate){
  
    
    var Needs = GodGate.gateRequireMents[gate];
    
    if(!Needs)
        return ;
    
    if(Number(player.porm) < Needs.porm){
        
        alert_box.failMessage(`لا يمكنك فتح البوابة و رتبتك اقل من ${pormotion[Needs.porm].ar_title}`);
        return ;
        
    }
    
    if(Number(GodGate.playerGate.points) < Needs.points){
        alert_box.failMessage(`ليس لديك (${Needs.points}) نقطة لفتح البوابة`);
        return ;
    }
    
    
    alert_box.confirmDialog(`تأكيد فتح بوبة الاله ${GodGate.data.godName[gate]}` , function (){
       
        
        $.ajax({
           
            url: "api/godGate.php",
            data:{
                OPEN_GOD_GATE: true,
                gate:gate,
                id_player: ID_PLAYER,
                    token:TOKEN
            },
            type: 'POST',
            beforeSend: function (xhr) {
                
            },
            success: function (data, textStatus, jqXHR) {
                
                if(!isJson(data)){
                    alert(data);
                    return ;
                }
                
                var jsonData = JSON.parse(data);
                
                if(jsonData.state === "error_1"){
                    
                    alert_box.confirmMessage(`لا يمكنك فتح البوابة و رتبتك اقل من ${pormotion[Needs.porm].ar_title}`);
                    
                }else if(jsonData.state === "error_2"){
                    
                    alert_box.confirmMessage(`ليس لديك (${Needs.points}) نقطة لفتح البوابة`);
                    
                }else if(jsonData.state === "ok"){
                    
                    GodGate.playerGate[gate] = jsonData.score;
                    
                    GodGate.playerGate[("god_"+gate)] = jsonData.gate;
                    
                    GodGate.playerGate.points -= Needs.points;
                    
                    $("#god-gate").replaceWith(GodGate.godGateContent());
                    alert_box.succesMessage("تم فتح البوابة بنجاح");
                    
                }
                
            },
            error: function (jqXHR, textStatus, errorThrown) {
                
            }
            
        });
        
    });
    
    
    
};



